import React from 'react';
import './gallery.scss';

// Data fasilitas restoran
const facilities = [
  {
    id: 1,
    image: '/images/restaurant-interior.jpg', // Gambar fasilitas
    title: 'Cozy Interior',
    description: 'Enjoy a warm and cozy atmosphere with Italian-inspired decor.'
  },
  {
    id: 2,
    image: '/images/chef-special.jpg', // Gambar fasilitas
    title: 'Chef Specials',
    description: 'Experience our chef’s special dishes prepared with authentic Italian ingredients.'
  },
  {
    id: 3,
    image: '/images/outdoor-seating.jpg', // Gambar fasilitas
    title: 'Outdoor Seating',
    description: 'Dine al fresco in our charming outdoor seating area.'
  },
  {
    id: 4,
    image: '/images/wine-selection.jpg', // Gambar fasilitas
    title: 'Extensive Wine Selection',
    description: 'Choose from a wide range of Italian wines to complement your meal.'
  }
];

function Gallery() {
  return (
    <div className="gallery">
      <h1 className="gallery-title">Our Facilities</h1>
      <div className="gallery-items">
        {facilities.map(facility => (
          <div key={facility.id} className="gallery-item">
            <img src={facility.image} alt={facility.title} className="gallery-image" />
            <div className="gallery-info">
              <h2 className="gallery-item-title">{facility.title}</h2>
              <p className="gallery-item-description">{facility.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Gallery;
