import React, { useState } from 'react';
import menuData from '../data/menuData';
import './menu.scss';

function Menu() {
  // State to keep track of quantities for each menu item
  const [quantities, setQuantities] = useState({
    pizzas: menuData.pizzas.reduce((acc, pizza) => {
      acc[pizza.name] = 0;
      return acc;
    }, {}),
    burgers: menuData.burgers.reduce((acc, burger) => {
      acc[burger.name] = 0;
      return acc;
    }, {}),
    pastas: menuData.pastas.reduce((acc, pasta) => {
      acc[pasta.name] = 0;
      return acc;
    }, {})
  });

  const handleQuantityChange = (category, name, increment) => {
    setQuantities(prevQuantities => ({
      ...prevQuantities,
      [category]: {
        ...prevQuantities[category],
        [name]: Math.max(prevQuantities[category][name] + increment, 0)
      }
    }));
  };

  return (
    <div className="menu">
      {['pizzas', 'burgers', 'pastas'].map(category => (
        <section key={category} className="menu-category">
          <h2>{category.charAt(0).toUpperCase() + category.slice(1)}</h2>
          <div className="menu-items">
            {menuData[category].map(item => (
              <div key={item.name} className="menu-item">
                <img src={item.image} alt={item.name} className="menu-item-image" />
                <div className="menu-item-details">
                  <h3 className="menu-item-name">{item.name}</h3>
                  <p className="menu-item-description">{item.description}</p>
                  <p className="menu-item-price">{item.price}</p>
                  <div className="menu-item-quantity">
                    <button onClick={() => handleQuantityChange(category, item.name, -1)}>-</button>
                    <span>{quantities[category][item.name]}</span>
                    <button onClick={() => handleQuantityChange(category, item.name, 1)}>+</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </section>
      ))}
    </div>
  );
}

export default Menu;
