import React, { useState } from 'react';
import menuData from '../data/Data';
import './menu.scss';

function Menu() {
  // State untuk menyimpan data pizza
  const [pizzas, setPizzas] = useState(menuData.pizzas);

  // Fungsi untuk menambahkan pizza baru
  const handleCreate = () => {
    const newPizza = {
      name: "New Pizza",
      description: "Description of new pizza",
      price: "$0.00",
      image: "https://via.placeholder.com/150"
    };
    setPizzas([...pizzas, newPizza]);
  };

  // Fungsi untuk menghapus pizza berdasarkan index
  const handleDelete = (index) => {
    const updatedPizzas = pizzas.filter((_, i) => i !== index);
    setPizzas(updatedPizzas);
  };

  // Fungsi untuk mengedit pizza
  const handleUpdate = (index) => {
    const updatedPizzas = pizzas.map((pizza, i) => {
      if (i === index) {
        return { ...pizza, name: "Updated Pizza", price: "$10.00" };
      }
      return pizza;
    });
    setPizzas(updatedPizzas);
  };

  return (
    <div className="menu">
      <section className="menu-category">
        <h2>Pizzas</h2>
        <hr />
        <button onClick={handleCreate} className="menu-button create">Add New Pizza</button>
        <div className="menu-items">
          {pizzas.map((pizza, index) => (
            <div key={index} className="menu-item">
              <img src={pizza.image} alt={pizza.name} className="menu-item-image" />
              <div className="menu-item-details">
                <h3 className="menu-item-name">{pizza.name}</h3>
                <p className="menu-item-description">{pizza.description}</p>
                <p className="menu-item-price">{pizza.price}</p>
                {/* Tombol CRUD */}
                <div className="menu-item-actions">
                  <button onClick={() => handleUpdate(index)} className="menu-button update">Update</button>
                  <button onClick={() => handleDelete(index)} className="menu-button delete">Delete</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Menu;
