import React, { useEffect, useRef, useState } from 'react';
import './VideosSection.scss'; // Menggunakan SCSS untuk styling

const ClientsSection = () => {
  const videoRef = useRef(null); // Referensi untuk video
  const [isVisible, setIsVisible] = useState(false); // Status visibilitas video

  useEffect(() => {
    const handleScroll = () => {
      const videoElement = videoRef.current;
      const rect = videoElement.getBoundingClientRect();
      const isVisible = rect.top < window.innerHeight * 0.5 && rect.bottom > window.innerHeight * 0.5;

      setIsVisible(isVisible);

      if (isVisible) {
        videoElement.play(); // Memutar video jika terlihat di viewport
      } else {
        videoElement.pause(); // Memberhentikan video jika tidak terlihat di viewport
      }
    };

    // Menambahkan event listener scroll
    window.addEventListener('scroll', handleScroll);

    // Memeriksa saat pertama kali komponen dimuat
    handleScroll();

    // Membersihkan event listener saat komponen di-unmount
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="clients-section">
      {/* Garis pemisah pertama */}
      <hr className="separator" />
      {/* Garis pemisah kedua */}
      <hr className="separator" />
      <h2 className="clients-title">Clients Preview</h2>
      {/* Teks tambahan di bawah judul */}
      <p className="clients-description">Here is a preview of what our clients have experienced with our services. Watch the videos to see their feedback and testimonials.</p>
      {/* Video pertama */}
      <div className={`video-container ${isVisible ? 'video-visible' : ''}`}>
        <video ref={videoRef} width="100%" height="auto" controls>
          <source src="./videos/video1.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>
    </div>
  );
};

export default ClientsSection;
