import React, { useState } from 'react';
import { FaShoppingCart } from 'react-icons/fa';
import menuData from '../data/menuData';
import './menu.scss';

function Menu() {
  // State to manage quantities
  const [quantities, setQuantities] = useState({
    pizzas: Array(menuData.pizzas.length).fill(0),
    burgers: Array(menuData.burgers.length).fill(0),
    pastas: Array(menuData.pastas.length).fill(0)
  });

  const handleQuantityChange = (category, index, amount) => {
    const newQuantities = { ...quantities };
    newQuantities[category][index] = Math.max(0, newQuantities[category][index] + amount);
    setQuantities(newQuantities);
  };

  const renderMenuItems = (category, items) => {
    return items.map((item, index) => (
      <div key={index} className="menu-item">
        <img src={item.image} alt={item.name} className="menu-item-image" />
        <div className="menu-item-info">
          <h3 className="menu-item-name">{item.name}</h3>
          <p className="menu-item-description">{item.description}</p>
          <p className="menu-item-price">${item.price}</p>
          <div className="menu-item-actions">
            <button
              className="quantity-button"
              onClick={() => handleQuantityChange(category, index, -1)}
            >
              -
            </button>
            <span className="quantity-display">
              {quantities[category][index]}
            </span>
            <button
              className="quantity-button"
              onClick={() => handleQuantityChange(category, index, 1)}
            >
              +
            </button>
            <button className="cart-button">
              <FaShoppingCart />
            </button>
          </div>
        </div>
      </div>
    ));
  };

  return (
    <div className="menu">
      <section className="menu-category">
        <h2>Pizzas</h2>
        <div className="menu-items">
          {renderMenuItems('pizzas', menuData.pizzas)}
        </div>
      </section>

      <section className="menu-category">
        <h2>Burgers</h2>
        <div className="menu-items">
          {renderMenuItems('burgers', menuData.burgers)}
        </div>
      </section>

      <section className="menu-category">
        <h2>Pastas</h2>
        <div className="menu-items">
          {renderMenuItems('pastas', menuData.pastas)}
        </div>
      </section>
    </div>
  );
}

export default Menu;
