import React, { useState } from 'react';
import menuData from '../data/Data';
import './menu.scss';

function Menu() {
  const [pizzas, setPizzas] = useState(menuData.pizzas);
  const [burgers, setBurgers] = useState(menuData.burgers);
  const [pastas, setPastas] = useState(menuData.pastas);
  const [editIndex, setEditIndex] = useState({ type: null, index: null });

  // Data default untuk setiap kategori
  const defaultPizza = {
    name: 'New Pizza',
    description: 'A delicious new pizza!',
    price: '$12.99',
    image: 'https://cdn1-production-images-kly.akamaized.net/5Rgd9MT6ayaE2xEUHS3XWIzpc6w=/0x281:4653x2903/800x450/filters:quality(75):strip_icc():format(webp)/kly-media-production/medias/3049433/original/093267200_1581580862-shutterstock_294636281.jpg', // Gambar default pizza
  };

  const defaultBurger = {
    name: 'New Burger',
    description: 'A tasty new burger!',
    price: '$8.99',
    image: 'https://www.blibli.com/friends-backend/wp-content/uploads/2023/08/COVER.jpg', // Gambar default burger
  };

  const defaultPasta = {
    name: 'New Pasta',
    description: 'A savory new pasta!',
    price: '$10.99',
    image: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcR03tTVpH0tdtfIVzmZgKEEf4r5e1bR-gfFpA&s', // Gambar default pasta
  };

  // Enable Edit Mode
  const handleEditMode = (type, index) => {
    setEditIndex({ type, index });
  };

  // Save Changes
  const handleSave = () => {
    setEditIndex({ type: null, index: null });
  };

  // Handle Input Changes
  const handleChange = (type, index, field, value) => {
    if (type === 'pizza') {
      const updatedPizzas = [...pizzas];
      updatedPizzas[index] = { ...updatedPizzas[index], [field]: value };
      setPizzas(updatedPizzas);
    }
    if (type === 'burger') {
      const updatedBurgers = [...burgers];
      updatedBurgers[index] = { ...updatedBurgers[index], [field]: value };
      setBurgers(updatedBurgers);
    }
    if (type === 'pasta') {
      const updatedPastas = [...pastas];
      updatedPastas[index] = { ...updatedPastas[index], [field]: value };
      setPastas(updatedPastas);
    }
  };

  // Delete Item
  const handleDelete = (type, index) => {
    if (type === 'pizza') {
      const updatedPizzas = pizzas.filter((_, i) => i !== index);
      setPizzas(updatedPizzas);
    }
    if (type === 'burger') {
      const updatedBurgers = burgers.filter((_, i) => i !== index);
      setBurgers(updatedBurgers);
    }
    if (type === 'pasta') {
      const updatedPastas = pastas.filter((_, i) => i !== index);
      setPastas(updatedPastas);
    }
  };

  // Add Item
  const handleAddItem = (type) => {
    if (type === 'pizza') {
      setPizzas([...pizzas, defaultPizza]);
    }
    if (type === 'burger') {
      setBurgers([...burgers, defaultBurger]);
    }
    if (type === 'pasta') {
      setPastas([...pastas, defaultPasta]);
    }
  };

  return (
    <div className="menu">
      {/* Pizzas Section */}
      <section id="menu-pizzas" className="menu-category">
        <h2>Pizzas</h2>
        <hr />
        <button className="add-btn" onClick={() => handleAddItem('pizza')}>Add Pizza</button>
        <div className="menu-items">
          {pizzas.map((pizza, index) => (
            <div key={index} className="menu-item">
              <img src={pizza.image} alt={pizza.name} className="menu-item-image" />
              <div className="menu-item-details">
                {editIndex.type === 'pizza' && editIndex.index === index ? (
                  <>
                    <input
                      type="text"
                      value={pizza.name}
                      onChange={(e) => handleChange('pizza', index, 'name', e.target.value)}
                    />
                    <textarea
                      value={pizza.description}
                      onChange={(e) => handleChange('pizza', index, 'description', e.target.value)}
                    ></textarea>
                    <button className='save-btn' onClick={handleSave}>Save</button>
                  </>
                ) : (
                  <>
                    <h3>{pizza.name}</h3>
                    <p>{pizza.description}</p>
                    <button className='edit-btn' onClick={() => handleEditMode('pizza', index)}>Edit</button>
                    <button className='delete-btn' onClick={() => handleDelete('pizza', index)}>Delete</button>
                  </>
                )}
                <p className="menu-item-price">{pizza.price}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Burgers Section */}
      <section id="menu-burgers" className="menu-category">
        <h2>Burgers</h2>
        <hr />
        <button className="add-btn" onClick={() => handleAddItem('burger')}>Add Burger</button>
        <div className="menu-items">
          {burgers.map((burger, index) => (
            <div key={index} className="menu-item">
              <img src={burger.image} alt={burger.name} className="menu-item-image" />
              <div className="menu-item-details">
                {editIndex.type === 'burger' && editIndex.index === index ? (
                  <>
                    <input
                      type="text"
                      value={burger.name}
                      onChange={(e) => handleChange('burger', index, 'name', e.target.value)}
                    />
                    <textarea
                      value={burger.description}
                      onChange={(e) => handleChange('burger', index, 'description', e.target.value)}
                    ></textarea>
                    <button className='save-btn' onClick={handleSave}>Save</button>
                  </>
                ) : (
                  <>
                    <h3>{burger.name}</h3>
                    <p>{burger.description}</p>
                    <button className='edit-btn' onClick={() => handleEditMode('burger', index)}>Edit</button>
                    <button className='delete-btn' onClick={() => handleDelete('burger', index)}>Delete</button>
                  </>
                )}
                <p className="menu-item-price">{burger.price}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Pastas Section */}
      <section id="menu-pastas" className="menu-category">
        <h2>Pastas</h2>
        <hr />
        <button className="add-btn" onClick={() => handleAddItem('pasta')}>Add Pasta</button>
        <div className="menu-items">
          {pastas.map((pasta, index) => (
            <div key={index} className="menu-item">
              <img src={pasta.image} alt={pasta.name} className="menu-item-image" />
              <div className="menu-item-details">
                {editIndex.type === 'pasta' && editIndex.index === index ? (
                  <>
                    <input
                      type="text"
                      value={pasta.name}
                      onChange={(e) => handleChange('pasta', index, 'name', e.target.value)}
                    />
                    <textarea
                      value={pasta.description}
                      onChange={(e) => handleChange('pasta', index, 'description', e.target.value)}
                    ></textarea>
                    <button onClick={handleSave}>Save</button>
                  </>
                ) : (
                  <>
                    <h3>{pasta.name}</h3>
                    <p>{pasta.description}</p>
                    <button className='edit-btn' onClick={() => handleEditMode('pasta', index)}>Edit</button>
                    <button className='delete-btn' onClick={() => handleDelete('pasta', index)}>Delete</button>
                  </>
                )}
                <p className="menu-item-price">{pasta.price}</p>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Menu;
