import React, { useState } from 'react';
import menuData from '../data/Data';
import './menu.scss';

function Menu() {
  const [pizzas, setPizzas] = useState(menuData.pizzas);
  const [burgers, setBurgers] = useState(menuData.burgers);
  const [pastas, setPastas] = useState(menuData.pastas);
  const [editIndex, setEditIndex] = useState({ type: null, index: null });

  // Enable Edit Mode
  const handleEditMode = (type, index) => {
    setEditIndex({ type, index });
  };

  // Save Changes
  const handleSave = () => {
    setEditIndex({ type: null, index: null });
  };

  // Handle Input Changes
  const handleChange = (type, index, field, value) => {
    if (type === 'pizza') {
      const updatedPizzas = [...pizzas];
      updatedPizzas[index] = { ...updatedPizzas[index], [field]: value };
      setPizzas(updatedPizzas);
    }
    if (type === 'burger') {
      const updatedBurgers = [...burgers];
      updatedBurgers[index] = { ...updatedBurgers[index], [field]: value };
      setBurgers(updatedBurgers);
    }
    if (type === 'pasta') {
      const updatedPastas = [...pastas];
      updatedPastas[index] = { ...updatedPastas[index], [field]: value };
      setPastas(updatedPastas);
    }
  };

  // DELETE
  const handleDeleteItem = (type, index) => {
    if (type === 'pizza') setPizzas(pizzas.filter((_, i) => i !== index));
    if (type === 'burger') setBurgers(burgers.filter((_, i) => i !== index));
    if (type === 'pasta') setPastas(pastas.filter((_, i) => i !== index));
  };

  return (
    <div className="menu">
      {/* Pizzas Section */}
      <section id="menu-pizzas" className="menu-category">
        <h2>Pizzas</h2>
        <button onClick={() => handleAddItem('pizza')}>Add Pizza</button>
        <hr />
        <div className="menu-items">
          {pizzas.map((pizza, index) => (
            <div key={index} className="menu-item">
              <img src={pizza.image} alt={pizza.name} className="menu-item-image" />
              <div className="menu-item-details">
                <input
                  type="text"
                  value={pizza.name}
                  onChange={(e) => handleUpdateItem('pizza', index, 'name', e.target.value)}
                  className="menu-item-name-input"
                />
                <textarea
                  value={pizza.description}
                  onChange={(e) => handleUpdateItem('pizza', index, 'description', e.target.value)}
                  className="menu-item-description-input"
                ></textarea>
                <p className="menu-item-price">{pizza.price}</p>
                <button onClick={() => handleDeleteItem('pizza', index)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Burgers Section */}
      <section id="menu-burgers" className="menu-category">
        <h2>Burgers</h2>
        <button onClick={() => handleAddItem('burger')}>Add Burger</button>
        <hr className="garis" />
        <div className="menu-items">
          {burgers.map((burger, index) => (
            <div key={index} className="menu-item">
              <img src={burger.image} alt={burger.name} className="menu-item-image" />
              <div className="menu-item-details">
                <input
                  type="text"
                  value={burger.name}
                  onChange={(e) => handleUpdateItem('burger', index, 'name', e.target.value)}
                  className="menu-item-name-input"
                />
                <textarea
                  value={burger.description}
                  onChange={(e) => handleUpdateItem('burger', index, 'description', e.target.value)}
                  className="menu-item-description-input"
                ></textarea>
                <p className="menu-item-price">{burger.price}</p>
                <button onClick={() => handleDeleteItem('burger', index)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Pastas Section */}
      <section id="menu-pastas" className="menu-category">
        <h2>Pastas</h2>
        <button onClick={() => handleAddItem('pasta')}>Add Pasta</button>
        <hr />
        <div className="menu-items">
          {pastas.map((pasta, index) => (
            <div key={index} className="menu-item">
              <img src={pasta.image} alt={pasta.name} className="menu-item-image" />
              <div className="menu-item-details">
                <input
                  type="text"
                  value={pasta.name}
                  onChange={(e) => handleUpdateItem('pasta', index, 'name', e.target.value)}
                  className="menu-item-name-input"
                />
                <textarea
                  value={pasta.description}
                  onChange={(e) => handleUpdateItem('pasta', index, 'description', e.target.value)}
                  className="menu-item-description-input"
                ></textarea>
                <p className="menu-item-price">{pasta.price}</p>
                <button onClick={() => handleDeleteItem('pasta', index)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Menu;
