import React, { useEffect } from 'react';
import { Link } from 'react-router-dom';
import './navbar.scss';

function Navbar() {
  useEffect(() => {
    const popUpElements = document.querySelectorAll('.pop-up');

    const observer = new IntersectionObserver((entries) => {
      entries.forEach(entry => {
        if (entry.isIntersecting) {
          entry.target.classList.add('active');
        } else {
          entry.target.classList.remove('active');
        }
      });
    });

    popUpElements.forEach(element => {
      observer.observe(element);
    });

    return () => {
      popUpElements.forEach(element => {
        observer.unobserve(element);
      });
    };
  }, []);

  return (
    <nav className="navbar">
      <div className="logo-container pop-up">
        <img src="/images/logo.jpg" alt="Bella Cucina Logo" className="logo" />
        <span className="logo-text">Bella Cucina</span>
      </div>
      <ul className="menu">
        <li className="pop-up"><Link to="/" className="nav-link">Home</Link></li>
        <li className="pop-up"><Link to="/menu" className="nav-link">Menu</Link></li>
        <li className="pop-up"><Link to="/about-us" className="nav-link">About Us</Link></li>
        <li className="pop-up"><Link to="/location" className="nav-link">Location</Link></li>
        <li className="pop-up"><Link to="/gallery" className="nav-link">Gallery</Link></li>
      </ul>
    </nav>
  );
}

export default Navbar;
