import React, { useState } from 'react';
import menuData from '../data/Data';
import './menu.scss';

function Menu() {
  const [pizzaItems, setPizzaItems] = useState(menuData.pizzas);
  const [burgerItems, setBurgerItems] = useState(menuData.burgers);
  const [pastaItems, setPastaItems] = useState(menuData.pastas);

  const [editItem, setEditItem] = useState(null);
  const [newItem, setNewItem] = useState({
    name: '',
    description: '',
    image: '',
    price: ''
  });

  const handleAddItem = (category) => {
    if (category === 'pizza') {
      setPizzaItems([...pizzaItems, newItem]);
    } else if (category === 'burger') {
      setBurgerItems([...burgerItems, newItem]);
    } else if (category === 'pasta') {
      setPastaItems([...pastaItems, newItem]);
    }
    setNewItem({ name: '', description: '', image: '', price: '' });
  };

  const handleEditMode = (category, index) => {
    const items = category === 'pizza' ? pizzaItems : category === 'burger' ? burgerItems : pastaItems;
    const item = items[index];
    setEditItem({ ...item, index, category });
  };

  const handleSaveEdit = () => {
    const { category, index, name, description, image, price } = editItem;
    if (category === 'pizza') {
      pizzaItems[index] = { name, description, image, price };
      setPizzaItems([...pizzaItems]);
    } else if (category === 'burger') {
      burgerItems[index] = { name, description, image, price };
      setBurgerItems([...burgerItems]);
    } else if (category === 'pasta') {
      pastaItems[index] = { name, description, image, price };
      setPastaItems([...pastaItems]);
    }
    setEditItem(null);
  };

  const handleDelete = (category, index) => {
    if (category === 'pizza') {
      setPizzaItems(pizzaItems.filter((_, i) => i !== index));
    } else if (category === 'burger') {
      setBurgerItems(burgerItems.filter((_, i) => i !== index));
    } else if (category === 'pasta') {
      setPastaItems(pastaItems.filter((_, i) => i !== index));
    }
  };

  return (
    <div className="menu">
      {/* Menu Pizza */}
      <section id="menu-pizzas" className="menu-category">
        <h2>Pizzas</h2>
        <hr />
        <div className="menu-items">
          {pizzaItems.map((pizza, index) => (
            <div key={index} className="menu-item">
              <img src={pizza.image} alt={pizza.name} className="menu-item-image" />
              <div className="menu-item-details">
                <h3 className="menu-item-name">{pizza.name}</h3>
                <p className="menu-item-description">{pizza.description}</p>
                <p className="menu-item-price">{pizza.price}</p>
              </div>
              <button className="edit-btn" onClick={() => handleEditMode('pizza', index)}>Edit</button>
              <button className="delete-btn" onClick={() => handleDelete('pizza', index)}>Delete</button>
            </div>
          ))}
        </div>
        <button className="add-item-btn" onClick={() => handleAddItem('pizza')}>Add Pizza</button>
      </section>

      {/* Menu Burger */}
      <section id="menu-burgers" className="menu-category">
        <h2>Burgers</h2>
        <hr />
        <div className="menu-items">
          {burgerItems.map((burger, index) => (
            <div key={index} className="menu-item">
              <img src={burger.image} alt={burger.name} className="menu-item-image" />
              <div className="menu-item-details">
                <h3 className="menu-item-name">{burger.name}</h3>
                <p className="menu-item-description">{burger.description}</p>
                <p className="menu-item-price">{burger.price}</p>
              </div>
              <button className="edit-btn" onClick={() => handleEditMode('burger', index)}>Edit</button>
              <button className="delete-btn" onClick={() => handleDelete('burger', index)}>Delete</button>
            </div>
          ))}
        </div>
        <button className="add-item-btn" onClick={() => handleAddItem('burger')}>Add Burger</button>
      </section>

      {/* Menu Pasta */}
      <section id="menu-pastas" className="menu-category">
        <h2>Pastas</h2>
        <hr />
        <div className="menu-items">
          {pastaItems.map((pasta, index) => (
            <div key={index} className="menu-item">
              <img src={pasta.image} alt={pasta.name} className="menu-item-image" />
              <div className="menu-item-details">
                <h3 className="menu-item-name">{pasta.name}</h3>
                <p className="menu-item-description">{pasta.description}</p>
                <p className="menu-item-price">{pasta.price}</p>
              </div>
              <button className="edit-btn" onClick={() => handleEditMode('pasta', index)}>Edit</button>
              <button className="delete-btn" onClick={() => handleDelete('pasta', index)}>Delete</button>
            </div>
          ))}
        </div>
        <button className="add-item-btn" onClick={() => handleAddItem('pasta')}>Add Pasta</button>
      </section>

      {/* Edit Item Modal */}
      {editItem && (
        <div className="edit-modal">
          <div className="edit-modal-content">
            <h3>Edit {editItem.category}</h3>
            <input
              type="text"
              value={editItem.name}
              onChange={(e) => setEditItem({ ...editItem, name: e.target.value })}
              placeholder="Name"
            />
            <textarea
              value={editItem.description}
              onChange={(e) => setEditItem({ ...editItem, description: e.target.value })}
              placeholder="Description"
            />
            <input
              type="text"
              value={editItem.image}
              onChange={(e) => setEditItem({ ...editItem, image: e.target.value })}
              placeholder="Image URL"
            />
            <input
              type="text"
              value={editItem.price}
              onChange={(e) => setEditItem({ ...editItem, price: e.target.value })}
              placeholder="Price"
            />
            <button className="edit-btn" onClick={handleSaveEdit}>Save</button>
            <button className="delete-btn" onClick={() => setEditItem(null)}>Cancel</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default Menu;
