import React, { useState, useEffect } from 'react';
import './menu.scss';

function Menu() {
  const [pizzas, setPizzas] = useState([]);
  const [burgers, setBurgers] = useState([]);
  const [pastas, setPastas] = useState([]);
  const [editIndex, setEditIndex] = useState({ type: null, index: null });

  const fetchData = (category) => {
    fetch(`http://localhost/yourfile.php?category=${category}`)
      .then((response) => response.json())
      .then((data) => {
        if (category === 'pizzas') setPizzas(data);
        if (category === 'burgers') setBurgers(data);
        if (category === 'pastas') setPastas(data);
      });
  };

  // Mengambil data awal
  useEffect(() => {
    fetchData('pizzas');
    fetchData('burgers');
    fetchData('pastas');
  }, []);

  // Menambahkan Item
  const handleAddItem = (category) => {
    const newItem = {
      name: 'New Item',
      description: 'A new delicious item!',
      price: '$9.99',
      image: 'https://via.placeholder.com/150',
    };

    fetch('http://localhost/yourfile.php', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        category,
        ...newItem,
      }),
    })
      .then((response) => response.json())
      .then(() => fetchData(category)); // Refresh data after adding
  };

  // Edit Mode
  const handleEditMode = (category, index) => {
    setEditIndex({ type: category, index });
  };

  // Save Changes
  const handleSave = (category, index) => {
    const updatedItem = {
      id: category === 'pizzas' ? pizzas[index].id : category === 'burgers' ? burgers[index].id : pastas[index].id,
      name: category === 'pizzas' ? pizzas[index].name : category === 'burgers' ? burgers[index].name : pastas[index].name,
      description: category === 'pizzas' ? pizzas[index].description : category === 'burgers' ? burgers[index].description : pastas[index].description,
      price: category === 'pizzas' ? pizzas[index].price : category === 'burgers' ? burgers[index].price : pastas[index].price,
      image: category === 'pizzas' ? pizzas[index].image : category === 'burgers' ? burgers[index].image : pastas[index].image,
    };

    fetch('http://localhost/yourfile.php', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        category,
        ...updatedItem,
      }),
    })
      .then((response) => response.json())
      .then(() => {
        setEditIndex({ type: null, index: null });
        fetchData(category); // Refresh data after saving
      });
  };

  // Handle Input Changes
  const handleChange = (category, index, field, value) => {
    if (category === 'pizza') {
      const updatedPizzas = [...pizzas];
      updatedPizzas[index] = { ...updatedPizzas[index], [field]: value };
      setPizzas(updatedPizzas);
    }
    if (category === 'burger') {
      const updatedBurgers = [...burgers];
      updatedBurgers[index] = { ...updatedBurgers[index], [field]: value };
      setBurgers(updatedBurgers);
    }
    if (category === 'pasta') {
      const updatedPastas = [...pastas];
      updatedPastas[index] = { ...updatedPastas[index], [field]: value };
      setPastas(updatedPastas);
    }
  };

  // Delete Item
  const handleDelete = (category, index) => {
    const itemId = category === 'pizza' ? pizzas[index].id : category === 'burger' ? burgers[index].id : pastas[index].id;

    fetch('http://localhost/backend/yourfile.php', {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        category,
        id: itemId,
      }),
    })
      .then((response) => response.json())
      .then(() => fetchData(category)); // Refresh data after deleting
  };

  return (
    <div className="menu">
      {/* Pizzas Section */}
      <section id="menu-pizzas" className="menu-category">
        <h2>Pizzas</h2>
        <hr />
        <button className="add-btn" onClick={() => handleAddItem('pizzas')}>Add Pizza</button>
        <div className="menu-items">
          {pizzas.map((pizza, index) => (
            <div key={pizza.id} className="menu-item">
              <img src={pizza.image} alt={pizza.name} className="menu-item-image" />
              <div className="menu-item-details">
                {editIndex.type === 'pizzas' && editIndex.index === index ? (
                  <>
                    <input
                      type="text"
                      value={pizza.name}
                      onChange={(e) => handleChange('pizza', index, 'name', e.target.value)}
                    />
                    <textarea
                      value={pizza.description}
                      onChange={(e) => handleChange('pizza', index, 'description', e.target.value)}
                    ></textarea>
                    <button className="save-btn" onClick={() => handleSave('pizzas', index)}>Save</button>
                  </>
                ) : (
                  <>
                    <h3>{pizza.name}</h3>
                    <p>{pizza.description}</p>
                    <button className="edit-btn" onClick={() => handleEditMode('pizzas', index)}>Edit</button>
                    <button className="delete-btn" onClick={() => handleDelete('pizzas', index)}>Delete</button>
                  </>
                )}
                <p className="menu-item-price">{pizza.price}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Burgers Section */}
      <section id="menu-burgers" className="menu-category">
        <h2>Burgers</h2>
        <hr />
        <button className="add-btn" onClick={() => handleAddItem('burgers')}>Add Burger</button>
        <div className="menu-items">
          {burgers.map((burger, index) => (
            <div key={burger.id} className="menu-item">
              <img src={burger.image} alt={burger.name} className="menu-item-image" />
              <div className="menu-item-details">
                {editIndex.type === 'burgers' && editIndex.index === index ? (
                  <>
                    <input
                      type="text"
                      value={burger.name}
                      onChange={(e) => handleChange('burger', index, 'name', e.target.value)}
                    />
                    <textarea
                      value={burger.description}
                      onChange={(e) => handleChange('burger', index, 'description', e.target.value)}
                    ></textarea>
                    <button className="save-btn" onClick={() => handleSave('burgers', index)}>Save</button>
                  </>
                ) : (
                  <>
                    <h3>{burger.name}</h3>
                    <p>{burger.description}</p>
                    <button className="edit-btn" onClick={() => handleEditMode('burgers', index)}>Edit</button>
                    <button className="delete-btn" onClick={() => handleDelete('burgers', index)}>Delete</button>
                  </>
                )}
                <p className="menu-item-price">{burger.price}</p>
              </div>
            </div>
          ))}
        </div>
      </section>

      {/* Pastas Section */}
      <section id="menu-pastas" className="menu-category">
        <h2>Pastas</h2>
        <hr />
        <button className="add-btn" onClick={() => handleAddItem('pastas')}>Add Pasta</button>
        <div className="menu-items">
          {pastas.map((pasta, index) => (
            <div key={pasta.id} className="menu-item">
              <img src={pasta.image} alt={pasta.name} className="menu-item-image" />
              <div className="menu-item-details">
                {editIndex.type === 'pastas' && editIndex.index === index ? (
                  <>
                    <input
                      type="text"
                      value={pasta.name}
                      onChange={(e) => handleChange('pasta', index, 'name', e.target.value)}
                    />
                    <textarea
                      value={pasta.description}
                      onChange={(e) => handleChange('pasta', index, 'description', e.target.value)}
                    ></textarea>
                    <button className="save-btn" onClick={() => handleSave('pastas', index)}>Save</button>
                  </>
                ) : (
                  <>
                    <h3>{pasta.name}</h3>
                    <p>{pasta.description}</p>
                    <button className="edit-btn" onClick={() => handleEditMode('pastas', index)}>Edit</button>
                    <button className="delete-btn" onClick={() => handleDelete('pastas', index)}>Delete</button>
                  </>
                )}
                <p className="menu-item-price">{pasta.price}</p>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
}

export default Menu;
