import React from 'react';
import './gallery.scss';
import facilities from '../data/Facilities';

// Data fasilitas restoran

function Gallery() {
  return (
    <div className="gallery">
      <div className="gallery-items">
        {facilities.map(facility => (
          <div key={facilities} className="gallery-item">
            <img src={facilities} alt={facility.title} className="gallery-image" />
            <div className="gallery-info">
              <h2 className="gallery-item-title">{facility.title}</h2>
              <p className="gallery-item-description">{facility.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Gallery;
